import Phaser from "phaser";
import type { IElement, IElementPlacement } from "../types";
class SpaceScene extends Phaser.Scene {
  private player!: Phaser.Types.Physics.Arcade.SpriteWithDynamicBody;
  private cursors!: Phaser.Types.Input.Keyboard.CursorKeys;
  private nameContainer!: Phaser.GameObjects.Container;

  private readonly map: any;
  private readonly userAvatar: any;
  private readonly user: any;
  private readonly spaceId: string;

  private cursorImage?: Phaser.GameObjects.Image;
  private cursorElement?: IElement;
  private inDeleteMode: boolean = false;

  private static readonly PLAYER_SPEED = 200;
  private static readonly PLAYER_SIZE = { width: 40, height: 70 };
  private static readonly CAMERA_FOLLOW_SPEED = 0.08;
  private static readonly NAME_OFFSET_X = -25;

  private static readonly NAME_OFFSET_Y = -25;
  private static readonly ONLINE_DOT_OFFSET_X = -12;
  private static readonly ONLINE_DOT_RADIUS = 6;
  private static readonly ELEMENT_DEPTH = 5;
  private static readonly CURSOR_DEPTH = 1000;
  private static readonly NAME_DEPTH = 10;
  private gameObjects: Map<string, Phaser.GameObjects.Image> = new Map();
  private staticElements!: Phaser.Physics.Arcade.StaticGroup;
  private loadingIndicator: Phaser.GameObjects.Container | null = null;
  private isPlacingElement = false;

  constructor(spaceDetails: any, userAvatar: string, user: any, spaceId: any) {
    super("space-scene");
    this.map = spaceDetails;
    this.userAvatar = userAvatar;
    this.user = user;
    this.spaceId = spaceId;
  }

  preload(): void {
    this.loadAssets();
  }

  create(): void {
    this.setupGlobalReference();
    this.createBackground();
    this.setupPhysicsWorld();

    this.setupControls();
    this.createMapElements();
    this.setupEventListeners();
    this.setupClickHandler();
    this.setupRestoreMouseListener();
    this.setupDeleteElementListener();
  }

  update(): void {
    this.handlePlayerMovement();
    this.updateNameTagPosition();
    this.updateCursorImage();
  }

  private loadAssets(): void {
    const assets = {
      avatar:
        "https://dynamic-assets.gather.town/v2/sprite-profile/avatar-ty0CoZao8eIAgVnSKFg7.oQePaZCdEjy7bEWWLzb8.RjHZpJroqxJb85HeASYK.uelBgm8fS0qdpl7vVBoj.K6tbhYXBDUPkIw6ijSvq.RhTmC28lTQJ7MKeDlGwn.png?d=",
      background:
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/uploads/SxbUOEXSyod8nipD/Zoen2rBvuhksL5xO1MSY68",
      player: this.userAvatar,
      "brown desk":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/qRznTWoHmRlo11iHfDgtt",
      "white board":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/tEDZH5NiGNhcWW3NlPKC6",
      "chair straight":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/zAhXnynfAkVtwL1Z0wEOw",
      "brown desk flipped":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/XMCrJkQElJOCzXxtbMqSG",
      erasur:
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/XMCrJkQElJOCzXxtbMqSG",
      "barrier horizontal":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/4Bib6kipLfjyQW5xRSq-V",
      "barrier vertical":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/xofcdARkPcHveNgXloUEq",
      "courtyard wall":
        "data:image/png;base64,",
      "purple-chair-back":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/9oegGdgxeo-JeweMicea9",
      "purple-chair-front":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/GRgZHvsAfE2J245J52i5Q",
      "computer-front":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/HuRVFTJcLmrh2C2H7eBpX",
      "computer-back":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/nbouxOO2ulIwP8Lg8RAaz",
      "purplesofa-front":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/Ui2w_i-dTQUNj19y_AGAX",
      "purplesofa-back":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/0rfQGsoVYMUNnBODoe9Jt",
      "sofa-table-front":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/Evdk03yzF9B3TisiA4_oN",
      "plant-lily":
        "https://cdn.gather.town/storage.googleapis.com/gather-town.appspot.com/internal-dashboard/images/BNloQVfJcQUBDrJqHLdXD",
    };

    Object.entries(assets).forEach(([key, url]) => {
      this.load.image(key, url);
    });
  }

  private setupGlobalReference(): void {
    (window as any).phaserScene = this;
  }

  private createBackground(): void {
    const { width, height } = this.getWorldDimensions();

    this.add
      .image(0, 0, "background")
      .setOrigin(0)
      .setDisplaySize(width, height);
  }

  private setupPhysicsWorld(): void {
    const { width, height } = this.getWorldDimensions();
    this.cameras.main.setBounds(0, 0, width, height);
    this.physics.world.setBounds(0, 0, width, height);
    // this.physics.world.createDebugGraphic();
    this.staticElements = this.physics.add.staticGroup();
  }

  private createPlayer(x: number, y: number, avatarImageUrl: string): void {
    const avatarKey = `avatar-${Date.now()}`;
    this.load.image(avatarKey, avatarImageUrl);
    this.load.once("complete", () => {
      this.player = this.physics.add.sprite(x, y, avatarKey);
      this.player.setDisplaySize(
        SpaceScene.PLAYER_SIZE.width,
        SpaceScene.PLAYER_SIZE.height
      );
      const collisionWidth = SpaceScene.PLAYER_SIZE.width * 0.8;
      const collisionHeight = SpaceScene.PLAYER_SIZE.height * 1.8;
      this.player.body.setSize(collisionWidth, collisionHeight);
      const offsetX = 50;
      const offsetY = 110;
      this.player.body.setOffset(offsetX, offsetY);
      this.player.setCollideWorldBounds(true);
      this.physics.add.collider(this.player, this.staticElements);
      this.createNameTag();
      this.setupCamera();
    });
    this.load.start();
  }

  private userSprites: Record<
    string,
    Phaser.Types.Physics.Arcade.SpriteWithDynamicBody
  > = {};
  private userContainers: Record<string, Phaser.GameObjects.Container> = {};

  private renderUsers(userData: any): void {
    const { x, y, avatarImageUrl, userId, username } = userData;
    const avatarKey = `avatar-${userId}`;
    if (!this.textures.exists(avatarKey)) {
      this.load.image(avatarKey, avatarImageUrl);
      this.load.once("complete", () =>
        this.createUserSprite(x, y, avatarKey, userId, username)
      );
      this.load.start();
    } else {
      this.createUserSprite(x, y, avatarKey, userId, username);
    }
  }

  private removeUser(userId: string): void {
    const player = this.userSprites[userId];
    const container = this.userContainers[userId];

    if (player) {
      player.destroy();
      delete this.userSprites[userId];
    }

    if (container) {
      container.destroy();
      delete this.userContainers[userId];
    }
  }

  private createUserSprite(
    x: number,
    y: number,
    avatarKey: string,
    userId: string,
    username: string
  ) {
    const player = this.physics.add.sprite(x, y, avatarKey);
    player.setDisplaySize(
      SpaceScene.PLAYER_SIZE.width,
      SpaceScene.PLAYER_SIZE.height
    );
    const collisionWidth = SpaceScene.PLAYER_SIZE.width * 0.8;
    const collisionHeight = SpaceScene.PLAYER_SIZE.height * 1.8;
    player.body.setSize(collisionWidth, collisionHeight);
    player.body.setOffset(50, 110);
    player.setCollideWorldBounds(true);
    this.physics.add.collider(player, this.staticElements);
    const nameText = this.add
      .text(10, 0, username, {
        fontSize: "12px",
        fontFamily: "Arial",
        color: "#ffffff",
        backgroundColor: "#000",
      })
      .setOrigin(0, 0.5);
    const onlineDot = this.add.circle(
      SpaceScene.ONLINE_DOT_OFFSET_X,
      0,
      SpaceScene.ONLINE_DOT_RADIUS,
      0x00ff00
    );
    const nameContainer = this.add.container(
      player.x + SpaceScene.NAME_OFFSET_X,
      player.y + SpaceScene.NAME_OFFSET_Y,
      [onlineDot, nameText]
    );
    nameContainer.setDepth(SpaceScene.NAME_DEPTH);
    this.userSprites[userId] = player;
    this.userContainers[userId] = nameContainer;
  }

  private createNameTag(): void {
    const nameText = this.add
      .text(0, 0, this.user.username, {
        fontSize: "12px",
        fontFamily: "Arial",
        color: "#ffffff",
        backgroundColor: "#000",
      })
      .setOrigin(0, 0.5);
    const onlineDot = this.add.circle(
      SpaceScene.ONLINE_DOT_OFFSET_X,
      0,
      SpaceScene.ONLINE_DOT_RADIUS,
      0x00ff00
    );
    this.nameContainer = this.add.container(
      this.player.x + SpaceScene.NAME_OFFSET_X,
      this.player.y + SpaceScene.NAME_OFFSET_Y,
      [onlineDot, nameText]
    );
    this.nameContainer.setDepth(SpaceScene.NAME_DEPTH);
  }

  private setupCamera(): void {
    this.cameras.main.startFollow(
      this.player,
      true,
      SpaceScene.CAMERA_FOLLOW_SPEED,
      SpaceScene.CAMERA_FOLLOW_SPEED
    );
  }

  private setupControls(): void {
    this.cursors = this.input.keyboard!.createCursorKeys();
  }

  private createMapElements(): void {
    this.map.elements.forEach((elementData: any) => {
      this.createMapElement({ ...elementData });
    });
  }

  private createMapElement(elementData: IElementPlacement): void {
    const { x, y, element } = elementData;
    const { width, height, name } = element;
    const gameElement = this.add.image(x, y, name);
    gameElement.setDepth(SpaceScene.ELEMENT_DEPTH);
    if (width && height) {
      gameElement.setDisplaySize(width, height);
    }
    let offsetY = 0;
    switch (name) {
      case "brown desk":
      case "brown desk flipped":
        offsetY = 15;
        break;
      case "chair straight":
        offsetY = 3;
        break;
      case "white board":
        offsetY = 8;
        break;
      case "purplesofa-back":
        offsetY = -20;
        break;
      case "purplesofa-front":
        offsetY = -20;
        break;
      default:
        offsetY = 0;
    }
    if (!element.static) {
      const physicsRect = this.add.rectangle(
        x,
        y - offsetY,
        width || gameElement.displayWidth,
        height * 0.6 || gameElement.displayHeight * 0.6
      );
      physicsRect.setVisible(false);
      this.physics.add.existing(physicsRect, true);
      this.staticElements.add(physicsRect);
      (gameElement as any).physicsBody = physicsRect;
    }
    if (elementData._id) {
      this.gameObjects.set(elementData._id, gameElement);
    }
    gameElement.setData("elementData", elementData);
    (elementData as any).gameObject = gameElement;
    gameElement.setInteractive();
    if (!element.visible) {
      gameElement.setVisible(false);
    }
    if (!element.static) {
      gameElement.on("pointerover", () => {
        if (this.inDeleteMode) {
          gameElement.setTint(0xff0000);
        }
      });
      gameElement.on("pointerout", () => {
        if (this.inDeleteMode) {
          gameElement.clearTint();
        }
      });
      gameElement.on("pointerdown", () => {
        const data = gameElement.getData("elementData");
        if (this.inDeleteMode && !this.loadingIndicator) {
          this.createLoadingIndicator();
          setTimeout(() => {
            this.hideLoadingIndicator();
          }, 1000);
          const deleteEvent = new CustomEvent("ElementDeleted", {
            detail: {
              elementPlacementId: data._id,
              spaceId: this.spaceId,
            },
          });
          window.dispatchEvent(deleteEvent);
        }
      });
    }
  }

  private setupEventListeners(): void {
    this.setupPlaceElementListener();
    this.setupImagePickupListener();
    this.setupDeleteModeListener();
    this.setExitDeleteModeListener();
    this.setupSpaceJoinedListener();
    this.setupUserJoinedListener();
    this.setupUserMovementListener();
    this.setupUserLeftListener();
  }

  private setupPlaceElementListener(): void {
    window.addEventListener("placeElementInPhaser", (event: any) => {
      const detail = event.detail as any;
      this.createMapElement({
        _id: detail._id,
        x: detail.x,
        y: detail.y,
        element: detail.element,
      });
    });
  }

  private setupDeleteElementListener(): void {
    window.addEventListener("DeleteElementInPhaser", (event: any) => {
      const deletedElementId = event.detail.deletedElementId;
      try {
        const gameObject = this.gameObjects.get(deletedElementId);
        if (gameObject) {
          if ((gameObject as any).physicsBody) {
            const physicsRect = (gameObject as any).physicsBody;
            this.staticElements.remove(physicsRect);
            physicsRect.destroy();
          }
          this.staticElements.remove(gameObject);
          gameObject.destroy();
          this.gameObjects.delete(deletedElementId);
          console.log(`Element ${deletedElementId} deleted successfully`);
        } else {
          console.warn(`Game object for element ${deletedElementId} not found`);
        }
        this.map.elements = this.map.elements.filter(
          (el: any) => el._id !== deletedElementId
        );
      } catch (error) {
        console.error("Error deleting element:", error);
      }
    });
  }

  private setupImagePickupListener(): void {
    window.addEventListener("imagePickedUp", (event: any) => {
      this.clearCursorState();
      const { elementData } = event.detail as any;
      this.setCursorImage(elementData);
      this.input.setDefaultCursor("none");
    });
  }

  private setupDeleteModeListener(): void {
    window.addEventListener("inDeleteMode", () => {
      console.log("Phaser in Delete Mode");
      this.clearCursorState();
      this.input.setDefaultCursor("default");
      this.inDeleteMode = true;
    });
  }

  private setExitDeleteModeListener(): void {
    window.addEventListener("exitDeleteMode", () => {
      this.clearCursorState();
      this.input.setDefaultCursor("default");
      this.inDeleteMode = false;
    });
  }

  private setupSpaceJoinedListener(): void {
    window.addEventListener("space-joined", (event: any) => {
      const { payload } = event.detail;
      console.log("Space joined payload:", payload);
      this.createPlayer(
        payload.spawn.x,
        payload.spawn.y,
        payload.spawn.avatarImageUrl
      );
      if (payload.users.length > 0) {
        payload.users.forEach((userData: any) => {
          this.renderUsers(userData);
        });
      }
    });
  }

  private setupUserJoinedListener(): void {
    window.addEventListener("user-joined", (event: any) => {
      const { payload } = event.detail;
      console.log("user joined payload:", payload);
      this.renderUsers(payload);
    });
  }

  private setupUserLeftListener(): void {
    window.addEventListener("user-left", (event: any) => {
      const { payload } = event.detail;
      console.log("user Left payload:", payload);
      this.removeUser(payload.userId);
    });
  }

  private setupUserMovementListener(): void {
    window.addEventListener("movement", (event: any) => {
      const { payload } = event.detail;
      console.log("user movement payload:", payload);
      const { x, y, userId } = payload;
      const player = this.userSprites[userId];
      const nameContainer = this.userContainers[userId];
      if (player && nameContainer) {
        this.tweens.add({
          targets: player,
          x,
          y,
          duration: 100,
          ease: "Linear",
          onUpdate: () => {
            nameContainer.x = player.x + SpaceScene.NAME_OFFSET_X;
            nameContainer.y = player.y + SpaceScene.NAME_OFFSET_Y;
          },
        });
      }
    });
  }

  private setupRestoreMouseListener(): void {
    window.addEventListener("restoreMouse", () => {
      this.clearCursorState();
      this.input.setDefaultCursor("default");
    });
  }

  private setupClickHandler(): void {
    this.input.on("pointerdown", () => {
      if (
        this.cursorImage &&
        this.isValidPlacement() &&
        !this.loadingIndicator
      ) {
        this.dispatchElementPlacedEvent();
      }
    });
  }

  private clearCursorState(): void {
    if (this.cursorImage) {
      this.cursorImage.destroy();
      this.cursorImage = undefined;
    }
    this.cursorElement = undefined;
    this.hideLoadingIndicator();
  }

  private setCursorImage(imageData: any): void {
    this.cursorImage = this.add
      .image(0, 0, imageData.name)
      .setDepth(SpaceScene.CURSOR_DEPTH);
    this.cursorImage.setDisplaySize(imageData.width, imageData.height);
    this.cursorElement = imageData;
  }

  private isValidPlacement(): boolean {
    return !!this.cursorElement;
  }

  private dispatchElementPlacedEvent(): void {
    if (!this.cursorImage) return;
    this.isPlacingElement = true;
    this.createLoadingIndicator();
    setTimeout(() => {
      this.hideLoadingIndicator();
    }, 1000);
    const placementEvent = new CustomEvent("ElementPlaced", {
      detail: {
        element: this.cursorElement,
        spaceId: this.spaceId,
        x: this.cursorImage.x,
        y: this.cursorImage.y,
      },
    });
    window.dispatchEvent(placementEvent);
  }

  private lastMoveEventTime = 0;

  private dispatchPlayerMoved(): void {
    const now = Date.now();
    if (now - this.lastMoveEventTime > 100) {
      this.lastMoveEventTime = now;
      const playerMovedEvent = new CustomEvent("PlayerMoved", {
        detail: {
          x: this.player!.x,
          y: this.player!.y,
          spaceId: this.spaceId,
        },
      });
      window.dispatchEvent(playerMovedEvent);
    }
  }

  private handlePlayerMovement(): void {
    if (!this.player) return;
    const velocity = new Phaser.Math.Vector2(0, 0);
    if (this.cursors.left?.isDown) {
      this.dispatchPlayerMoved();
      velocity.x = -1;
    } else if (this.cursors.right?.isDown) {
      this.dispatchPlayerMoved();
      velocity.x = 1;
    }
    if (this.cursors.up?.isDown) {
      this.dispatchPlayerMoved();
      velocity.y = -1;
    } else if (this.cursors.down?.isDown) {
      this.dispatchPlayerMoved();
      velocity.y = 1;
    }
    velocity.normalize().scale(SpaceScene.PLAYER_SPEED);
    this.player.setVelocity(velocity.x, velocity.y);
  }

  private updateNameTagPosition(): void {
    if (!this.player) {
      return;
    }
    this.nameContainer.setPosition(
      this.player.x + SpaceScene.NAME_OFFSET_X,
      this.player.y + SpaceScene.NAME_OFFSET_Y
    );
  }

  private updateCursorImage(): void {
    if (!this.cursorImage) return;
    const pointer = this.input.activePointer;
    this.cursorImage.setPosition(pointer.worldX, pointer.worldY);
    if (this.loadingIndicator && this.isPlacingElement) {
      this.loadingIndicator.setPosition(
        pointer.worldX + 50,
        pointer.worldY + 50
      );
    }
  }

  private getWorldDimensions(): { width: number; height: number } {
    return { width: 1848, height: 1848 };
  }

  private createLoadingIndicator(): void {
    const pointer = this.input.activePointer;
    const bg = this.add.rectangle(0, 0, 60, 60, 0x000000, 0.6);
    bg.setStrokeStyle(1, 0xffffff, 0.15);
    bg.setOrigin(0.5);
    const spinner = this.add.graphics();
    spinner.lineStyle(3, 0xffffff, 0.9);
    spinner.beginPath();
    spinner.arc(0, 0, 14, 0, Math.PI * 1.35, false);
    spinner.strokePath();
    const text = this.add
      .text(0, 20, "...", {
        fontSize: "10px",
        color: "#e0e0e0",
        fontStyle: "lighter",
      })
      .setOrigin(0.5);
    this.loadingIndicator = this.add.container(pointer.worldX, pointer.worldY, [
      bg,
      spinner,
      text,
    ]);
    this.loadingIndicator.setDepth(SpaceScene.CURSOR_DEPTH + 1);
    this.tweens.add({
      targets: spinner,
      rotation: Math.PI * 2,
      duration: 900,
      repeat: -1,
      ease: "Linear",
    });
  }

  private hideLoadingIndicator(): void {
    if (this.loadingIndicator) {
      this.tweens.add({
        targets: this.loadingIndicator,
        alpha: 0,
        duration: 300,
        onComplete: () => {
          if (this.loadingIndicator) {
            this.loadingIndicator.destroy();
            this.loadingIndicator = null;
          }
          this.isPlacingElement = false;
        },
      });
    }
  }
}

export default SpaceScene;
